mapper.update:
update mes_po_return_vendor_all
ERP_FLAG_DATE = getdate(), ERP_FLAG = #{erpFlag}, error_infor = #{errorInfor},
where return_vendor_id = #{returnVendorId}

update mes_po_return_vendor_all
ERP_FLAG_DATE_WORK = getdate(), work_order_number = #{workOrderNumber}, ERP_FLAG_WORK = #{erpFlagWork}, error_infor = #{errorInfor},
where return_vendor_id = #{returnVendorId}

mapper.select:


     and prva.document_number = #{documentNumber}
     and prva.ITEM_NUMBER = #{itemNumber}
     and prva.ERP_FLAG_WORK = #{erpFlagWork}
     and prva.work_order_number = #{workOrderNumber}
     and prva.erp_flag = #{erpFlag}

order by prva.return_vendor_id desc
